/** @format */

var ww = $(window).width();
$(document).ready(function () {
  adjustMenu();

  $(".cat").click(function (e) {
    // cat class
    e.preventDefault();
    $(this).toggleClass("active");
    $(".sf-menu").toggle();
  });
});

function adjustMenu() {
  if (ww <= 740) {
    //change this to your breakpoint
    $(".sf-menu").hide();
    $(".cat").show();
    if (!$(".cat").hasClass("active")) {
      $(".sf-menu").hide();
    } else {
      $(".sf-menu").show();
    }
  } else {
    $(".sf-menu").show();
    $(".cat").hide();
  }
}

$(window).bind("resize orientationchange", function () {
  ww = document.body.clientWidth;
  adjustMenu();
});
